mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT * FROM M_reserve r LEFT OUTER JOIN M_movie m ON r.movie_num = m.movie_num LEFT OUTER JOIN m_time t ON r.time_num = t.time_num LEFT OUTER JOIN m_reservseat s on s.time_num = t.time_num LEFT OUTER JOIN m_theater th ON t.theater_num = th.theater_num ORDER BY r.movie_num DESC
WHERE movie_Date >= #{movieDate}
AND t.movie_date LIKE '%' || #{keyword} || '%'
AND m.movie_name LIKE '%' || #{keyword} || '%'
AND th.theater_name LIKE '%' || #{keyword} || '%'
ORDER BY t.movie_date DESC
ORDER BY m.movie_num DESC
ORDER BY th.theater_name ASC
)a)
where rnum >= #{start} AND rnum <= #{end}
