mapper.select:
SELECT * FROM m_board b JOIN m_member m 
			 ON b.mem_num=m.mem_num LEFT JOIN m_category c on b.cate_num=c.cate_num
			 WHERE b.board_num=#{board_num}

SELECT * FROM m_board b JOIN m_member m 
	  ON b.mem_num=m.mem_num LEFT JOIN m_category c on b.cate_num=c.cate_num
	  WHERE b.board_num=#{board_num}

SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT reply_num,
REPLACE(REPLACE(re_content,'<','&lt;'),'>','&gt;') re_content,
re_content, re_date, board_num, mem_num, id FROM m_reply JOIN m_member USING(mem_num) WHERE board_num = #{board_num} ORDER BY reply_num DESC)a) WHERE rnum >= #{start} AND rnum <= #{end}
